    // {x:0, y:0, w:600, h:400 }
    function clipImage(rect, src, callback) {
        const img = new Image();
        img.src = src;

        img.onload = () => {
            const canvas = document.createElement('canvas');
            const ctx = canvas.getContext('2d');
            canvas.width = rect.w;
            canvas.height = rect.h;
            ctx.drawImage(img, -rect.x, -rect.y, img.naturalWidth, img.naturalHeight);
            callback(canvas.toDataURL());
        };
    }

    // {x:0, y:0, w:600, h:400 }
    function clipEllipse(rect, src, callback) {
        const img = new Image();
        img.src = src;

        img.onload = () => {
            const canvas = document.createElement('canvas');
            const ctx = canvas.getContext('2d');
            canvas.width = rect.w;
            canvas.height = rect.h;
            const a = rect.w / 2;
            const b = rect.h / 2;
            const r = Math.max(a, b);
            const ratioX = a / r;
            const ratioY = b / r;
            ctx.save();
            ctx.beginPath();
            ctx.arc(a / ratioX, b / ratioY, r, 0, 2 * Math.PI, false);
            ctx.closePath();
            ctx.clip();
            ctx.drawImage(img, -rect.x, -rect.y, img.naturalWidth, img.naturalHeight);
            ctx.restore();
            callback(canvas.toDataURL());
        }
    }

    clipImage(
        { x: 200, y: 200, w: 400, h: 400 },
        'source.jpg',
        (url) => {
            const img = new Image();
            img.src = url;
            document.body.appendChild(img);
        }
    );

    clipEllipse(
