...

import androidx.room.ColumnInfo
import androidx.room.Entity
import androidx.room.PrimaryKey

@Entity(tableName = "api_data")
data class ApiSaveData(

    // @ColumnInfo(name = "id")
    @PrimaryKey
    var id: Int? = null,

